import{_ as a,c as e,o as i,a as r}from"./app.ddfa0823.js";const u=JSON.parse('{"title":"Single-spa","description":"","frontmatter":{},"headers":[],"relativePath":"guide/04_FRONTEND/07_Single-spa.md","lastUpdated":1673343212000}'),t={name:"guide/04_FRONTEND/07_Single-spa.md"},s=r('<h1 id="single-spa" tabindex="-1">Single-spa <a class="header-anchor" href="#single-spa" aria-hidden="true">#</a></h1><p><a name="IdlQX"></a></p><h2 id="特点" tabindex="-1">特点 <a class="header-anchor" href="#特点" aria-hidden="true">#</a></h2><ul><li>同一页面，不同框架，不用刷新页面</li><li>独立部署每一个单页面应用</li><li>新功能使用新框架，旧单页应用不用重写可以共存</li><li>改善初始加载时间，延迟加载代码</li></ul><p><a name="BQEwf"></a></p><h2 id="内容" tabindex="-1">内容 <a class="header-anchor" href="#内容" aria-hidden="true">#</a></h2><ul><li>app：每个应用都是完整的 SPA，可以响应 URL 路由事件，DOM 生命周期：初始化、挂载、卸载。没有各自的 HTML 页面</li><li>config 配置：html 向程序注册 <ul><li>A name</li><li>A function（加载应用程序的代码）</li><li>A function （确定应用程序何时处于活动状态/非活动状态）</li></ul></li></ul><p><a name="vkqrs"></a></p><h2 id="基本原理" tabindex="-1">基本原理 <a class="header-anchor" href="#基本原理" aria-hidden="true">#</a></h2><ul><li>portal 项目</li><li>app 项目</li></ul><br><br><p><a name="H98Ky"></a></p><h2 id="single-spa-test" tabindex="-1">Single-spa-test <a class="header-anchor" href="#single-spa-test" aria-hidden="true">#</a></h2><p><br>jquery或以前老项目逐步迁移迭代方案<br></p><p><a name="jASZu"></a></p><h3 id="single-spa-1" tabindex="-1">single-spa <a class="header-anchor" href="#single-spa-1" aria-hidden="true">#</a></h3><p><br>微前端<a href="https://zh-hans.single-spa.js.org/docs/getting-started-overview" target="_blank" rel="noreferrer">应用框架</a><br><br>使用其简单搭建出一个微前端架构<br></p><p><a name="sdWxQ"></a></p><h3 id="单页应用" tabindex="-1">单页应用 <a class="header-anchor" href="#单页应用" aria-hidden="true">#</a></h3><p><br>随着jquery老项目迁移到单页应用是需要迁移成本，使用微前端来<strong>逐步</strong>迁移是一种择优的方案<br></p><p><a name="oA6Dr"></a></p><h3 id="迁移步骤" tabindex="-1">迁移步骤 <a class="header-anchor" href="#迁移步骤" aria-hidden="true">#</a></h3><ul><li>建立 single-spa-config 配置</li><li>创建需要迁移单页应用的页面 <code>spa.html</code></li><li>单页应用的<strong>路由 base</strong>最好是设置成与老项目根目录<strong>同名</strong> <code>vue-template</code></li></ul><blockquote><p>项目地址： <a href="https://github.com/imondo/single-spa-test" target="_blank" rel="noreferrer">single-spa-test</a> 效果地址：<a href="https://imondo.github.io/single-spa-test/" target="_blank" rel="noreferrer">single-spa-test</a></p></blockquote>',25),n=[s];function l(d,h,p,o,c,g){return i(),e("div",null,n)}const b=a(t,[["render",l]]);export{u as __pageData,b as default};
