import{_ as a,c as n,o as s,a as p}from"./app.90b3aaf2.js";const A=JSON.parse('{"title":"win10 上 安装 nginx","description":"","frontmatter":{},"headers":[],"relativePath":"guide/07_PROGRAM/12_win10 上 安装 nginx.md","lastUpdated":1670822938000}'),e={name:"guide/07_PROGRAM/12_win10 上 安装 nginx.md"},l=p(`<h1 id="win10-上-安装-nginx" tabindex="-1">win10 上 安装 nginx <a class="header-anchor" href="#win10-上-安装-nginx" aria-hidden="true">#</a></h1><p><a name="ksfty"></a></p><h2 id="下载安装文件" tabindex="-1">下载安装文件 <a class="header-anchor" href="#下载安装文件" aria-hidden="true">#</a></h2><p><br>到<a href="https://www.cnblogs.com/qfb620/p/5508468.html" target="_blank" rel="noreferrer"> nginx 官网</a>上下载相应的安装包<br><img src="https://cdn.nlark.com/yuque/0/2020/jpeg/124135/1591066193664-099159ae-80f2-4f03-b5fd-1e87ad310695.jpeg#align=left&amp;display=inline&amp;height=321&amp;margin=%5Bobject%20Object%5D&amp;name=1591066180%281%29.jpg&amp;originHeight=641&amp;originWidth=753&amp;size=43021&amp;status=done&amp;style=none&amp;width=377" alt="1591066180(1).jpg"></p><p><a name="Dw7in"></a></p><h2 id="解压" tabindex="-1">解压 <a class="header-anchor" href="#解压" aria-hidden="true">#</a></h2><p><br><img src="https://cdn.nlark.com/yuque/0/2020/jpeg/124135/1591066289526-f4504762-294a-4e1f-887d-fa5556d8f94f.jpeg#align=left&amp;display=inline&amp;height=247&amp;margin=%5Bobject%20Object%5D&amp;name=1591066266%281%29.jpg&amp;originHeight=247&amp;originWidth=649&amp;size=11659&amp;status=done&amp;style=none&amp;width=649" alt="1591066266(1).jpg"><br>进入 conf ，找到 nginx.conf，可以修改监听端口，新增而外配置文件夹</p><p><img src="https://cdn.nlark.com/yuque/0/2020/png/124135/1591081474421-bb89a20d-1c48-4069-aebe-81c788e830cc.png#align=left&amp;display=inline&amp;height=567&amp;margin=%5Bobject%20Object%5D&amp;name=1591081461%281%29.png&amp;originHeight=567&amp;originWidth=628&amp;size=20788&amp;status=done&amp;style=none&amp;width=628" alt="1591081461(1).png"><a name="LLMmt"></a></p><h2 id="常用命令" tabindex="-1">常用命令 <a class="header-anchor" href="#常用命令" aria-hidden="true">#</a></h2><p>在 nginx 当前目录下执行命令<br></p><ul><li>start nginx.exe 启动</li><li>nginx.exe -s stop 结束</li><li>nginx.exe -s reload 重启</li></ul><p><a name="7bAuG"></a></p><h2 id="反向代理本地静态文件" tabindex="-1">反向代理本地静态文件 <a class="header-anchor" href="#反向代理本地静态文件" aria-hidden="true">#</a></h2><p><br>spa 项目本地打包后可以利用 nginx 反向代理，做成本地访问服务<br><br>在 vhost 文件夹下新建 dh.conf 文件<br></p><div class="language-nginx"><button title="Copy Code" class="copy"></button><span class="lang">nginx</span><pre class="shiki"><code><span class="line"><span style="color:#C792EA;">server</span><span style="color:#A6ACCD;"> {</span></span>
<span class="line"><span style="color:#89DDFF;">	listen </span><span style="color:#A6ACCD;">9527</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#89DDFF;">	server_name </span><span style="color:#A6ACCD;">localhost</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#C792EA;">location</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">/ </span><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">	root </span><span style="color:#A6ACCD;">/nginx/dh</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;"># 打包后的静态文件目录</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">	index </span><span style="color:#A6ACCD;">index.html index.htm</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">	try_files </span><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">uri </span><span style="color:#89DDFF;">$</span><span style="color:#A6ACCD;">uri/ /index.html</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">	}</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#C792EA;">location</span><span style="color:#A6ACCD;"> </span><span style="color:#FFCB6B;">/api </span><span style="color:#A6ACCD;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">	proxy_pass </span><span style="color:#A6ACCD;">http://www.dherp.com/index.php/api</span><span style="color:#89DDFF;">;</span><span style="color:#A6ACCD;"> </span><span style="color:#676E95;"># 反向代理到后端服务</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">	proxy_redirect </span><span style="color:#A6ACCD;">off</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#A6ACCD;">	}</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"></span></code></pre></div>`,15),t=[l];function o(i,r,c,d,h,y){return s(),n("div",null,t)}const C=a(e,[["render",o]]);export{A as __pageData,C as default};
